import thunkMiddleware from 'redux-thunk';
import {
    createStore,
    applyMiddleware,
    compose,
    Store as ReduxStore,
} from 'redux';
import rootReducer, { initialState } from './reducer/rootReducer';

const dev: boolean = process.env.NODE_ENV !== 'production';

export type Store = ReduxStore<typeof initialState>;

export default (state = initialState): Store => {
    const middlewares = dev ? [thunkMiddleware] : [];
    return createStore(rootReducer, { ...state }, compose(applyMiddleware(...middlewares)));
};
