import { TextField, ClickAwayListener } from "@material-ui/core"
import { useDispatch, useSelector } from 'react-redux';
import actions from '../store/actions';
import React from "react";
import Autocomplete from '@material-ui/lab/Autocomplete';
import { RootState } from "../store/reducer/rootReducer";
interface CountryType {
    name: string;
}


export default function Search() {
    const dispatch = useDispatch();
    const data: any = useSelector((state: RootState) => state.product);
    console.log(data, 'data');
    const [options, setOptions] = React.useState<CountryType[]>([]);

    React.useEffect(() => {

    }, []);

    const handleChange = (value: any) => {
        console.log(value, 'çhange');
    }
    return (
        // <ClickAwayListener onClickAway={(e) => handleClickAway(e, index)} >
        <Autocomplete
            getOptionSelected={(option: any, value: any) => option.name === value.name}
            getOptionLabel={(option: any) => option.name}
            options={data && data.length > 0 ? data : []}
            blurOnSelect
            freeSolo={true}
            onChange={(event, newValue) => handleChange(newValue && newValue.hasOwnProperty('name') ? newValue.name : '')}
            // inputValue={}
            onInputChange={(newValue, reason) => {

                if (reason.toLowerCase() === 'input') {
                    handleChange(newValue)
                } else if (reason.toLowerCase() === 'clear') {
                    handleChange('')
                }
            }}
            renderInput={(params) => <TextField {...params} margin="normal" />}
        />
        // </ClickAwayListener>
    )
}
