import Layout from "../components/Layout"
import { Grid, makeStyles, Divider, Typography, Button } from "@material-ui/core";
const useStyles = makeStyles(() => ({
    root: {
        display: "block",
        textTransform: 'uppercase'
    },
    card: {
        maxWidth: 400,
        margin: "auto",
        // height: '120px',
        transition: "0.3s",
        // backgroundColor: "#792141",
        boxShadow: "0 16px 70px -12.125px rgba(0,0,0,0.3)",
        "&:hover": {
            boxShadow: "0 16px 70px -12.125px rgba(0,0,0,0.3)"
        }
    },
    content: {
        textAlign: "left",
        // padding: 20,
        fontSize: '15px',
        fontWeight: 600,
        // textTransform: 'uppercase'
    },
    rootContainer: {
        display: "inline-flex",
        width: "100%",
        padding: "17px 40px 17px 22px",
        marginBottom: 10,
        height: 400
    },
    cardpadding: {
        padding: '0 30px 0 0',
        '&:last-child': {
            padding: '0 30px 0 0',
        }
    },
    media: {
        height: 250,
        width: 300,
        // paddingTop: '56.25%', // 16:9,
        backgroundSize: 'cover'
    },
    button: {
        margin: '8px',
    },
}));
const Cart = () => {
    const data: any = JSON.parse(sessionStorage.getItem('cart') ? sessionStorage.getItem('cart') : []);
    const classes = useStyles();
    console.log(data, 'data');

    const remove = () => {

    }
    return (
        <Layout>
            {data.length && data.map((item: any, index: number) => {
                <Grid item container direction="row" justify="space-between" className={classes.rootContainer} key={'cart' + index}>
                    <Grid item xs={5} style={{ maxHeight: 500 }}>
                        <img src={item.image} alt="src" height={100} />
                    </Grid>
                    <Grid item xs={1}>
                        <Divider orientation="vertical" flexItem style={{ height: '100%', textAlign: 'center' }} />
                    </Grid>
                    <Grid item xs={6} className={classes.content}>
                        <Grid item>
                            <Typography component='h1' style={{ textTransform: 'uppercase' }}>
                                {item.category}
                            </Typography>
                            <Typography component='h1'>
                                {item.title}
                            </Typography>
                        </Grid>
                        <Grid item>
                            <Typography component='h1'>
                                ${item.price}
                            </Typography>
                        </Grid>
                        <Grid item>
                            <Typography component='h1'>
                                {item.description}
                            </Typography>
                        </Grid>
                        <Grid item>
                            <Button
                                variant="contained"
                                color="primary"
                                className={classes.button}
                                // startIcon={<ShoppingCartIcon />}
                                onClick={() => remove(item._id)}
                            > Remove</Button>

                        </Grid>
                    </Grid>
                </Grid>
            })}
        </Layout>
    )
}
export default Cart;
