import { RootState } from "../store/reducer/rootReducer";
import { useSelector } from "react-redux";
import { Grid, Card, CardActionArea, CardMedia, CardContent, Typography, CardActions, Button, makeStyles } from "@material-ui/core";
import { CATEGORY } from "../utils/contants";
const useStyles = makeStyles((theme: any) => ({
    root: {
        display: "block",
        textTransform: 'uppercase'
    },
    card: {
        maxWidth: 400,
        margin: "auto",
        // height: '120px',
        transition: "0.3s",
        // backgroundColor: "#792141",
        boxShadow: "0 16px 70px -12.125px rgba(0,0,0,0.3)",
        "&:hover": {
            boxShadow: "0 16px 70px -12.125px rgba(0,0,0,0.3)"
        }
    },
    content: {
        textAlign: "center",
        padding: 10,
        fontSize: '20px',
        fontWeight: 600
    },
    rootContainer: {
        display: "flex",
        width: "100%",
        padding: "10px 0px 17px 0px",
        marginBottom: 10
    },
    cardpadding: {
        padding: '0 30px 0 0',
        '&:last-child': {
            padding: '0 30px 0 0',
        }
    },
    media: {
        height: 140,
        paddingTop: '56.25%', // 16:9
    },
}));
export const List = () => {
    const classes = useStyles();
    const product = useSelector((state: RootState) => state.product);
    const { data } = product;
    return (
        <Grid item container direction="row" justify="space-between" className={classes.rootContainer}>
            {data.length && data.map((item: any, index: number) => (
                <Grid item xs={4} className={classes.root} key={'cat' + index}>
                    <Card className={classes.root}>
                        <CardActionArea>
                            <CardMedia
                                className={classes.media}
                                image={item.image}
                                title="Contemplative Reptile"
                            />
                            {/* <CardContent>
                                <Typography gutterBottom variant="h5" component="h2">
                                    Lizard
          </Typography>
                                <Typography variant="body2" color="textSecondary" component="p">
                                    Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
                                    across all continents except Antarctica
          </Typography>
                            </CardContent> */}
                        </CardActionArea>
                    </Card>
                </Grid>
            ))}
        </Grid>
    )
}
export default List;
