import { AUTHENTICATE, DEAUTHENTICATE, USER } from '../actions/ActionsTypes';

export const initialState = {
    token: null,
    user: null,
};

export default (state = initialState, action: any) => {
    switch (action.type) {
        case AUTHENTICATE:
            return Object.assign({}, state, { token: action.payload });
        case USER:
            return Object.assign({}, state, { user: action.payload });
        case DEAUTHENTICATE:
            return { token: null };
        default:
            return state;
    }
};
