import Link from 'next/link';
import Head from 'next/head';
import { useSelector } from 'react-redux';
import { Input, makeStyles, Theme, createStyles } from '@material-ui/core';
import { RootState } from '../store/reducer/rootReducer';
import { useState } from 'react';


const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        inputWidth: {
            width: 'auto'
        },

    }),
);

const Layout = ({ children, title, deauthenticate }: any) => {
    const classes = useStyles();
    const result: any = useSelector((state: RootState) => state.authentication);
    const isAuthenticated = !!result.token;
    const [search, setSearch] = useState('');
    const handlechange = (value: string) => {
        setSearch(value);
    }
    return (
        <div>
            <Head>
                <title>{title}</title>
                <meta charSet="utf-8" />
                <meta name="viewport" content="initial-scale=1.0, width=device-width" />
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/bulma/0.6.2/css/bulma.min.css" />
            </Head>
            <div className="tabs is-centered">
                <ul>
                    <h3>Ecommerce Task</h3>
                    {/* <Link href="/"><a>Home</a></Link> */}
                    {/* {!isAuthenticated && <Link href="/signin"><a>Sign In</a></Link>}
                    {!isAuthenticated && <Link href="/signup"><a>Sign Up</a></Link>}
                    {isAuthenticated && <Link href="/users"><a>Profile</a></Link>}
                    {isAuthenticated && <li onClick={deauthenticate}><a>Sign Out</a></li>} */}
                    {/* <Input
                        className={"input " + classes.inputWidth}
                        type="text"
                        placeholder="Search"
                        required
                        value={search}

                        onChange={e => handlechange(e.target.value)}
                    /> */}
                </ul>
            </div>

            <div className="has-text-centered">
                {children}
            </div>
        </div>
    )
};



export default Layout;
