import { Grid, makeStyles, Paper, Card, CardContent, Typography, Divider, CardMedia } from "@material-ui/core";
import { CATEGORY } from "../utils/contants";

const useStyles = makeStyles((theme: any) => ({
    root: {
        display: "block",
        textTransform: 'uppercase'
    },
    card: {
        maxWidth: 400,
        margin: "auto",
        // height: '120px',
        transition: "0.3s",
        // backgroundColor: "#792141",
        boxShadow: "0 16px 70px -12.125px rgba(0,0,0,0.3)",
        "&:hover": {
            boxShadow: "0 16px 70px -12.125px rgba(0,0,0,0.3)"
        }
    },
    content: {
        textAlign: "center",
        padding: 10,
        fontSize: '20px',
        fontWeight: 600
    },
    rootContainer: {
        display: "flex",
        width: "100%",
        padding: "10px 0px 17px 0px",
        marginBottom: 10
    },
    cardpadding: {
        padding: '0 30px 0 0',
        '&:last-child': {
            padding: '0 30px 0 0',
        }
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
}));
export const Category = (props: any) => {
    const classes = useStyles();
    const handleClick = (value) => {
        console.log(value);

    }
    return (
        <Grid item container direction="row" justify="space-between" className={classes.rootContainer}>
            {CATEGORY.map((item: any, index: number) => (
                // <Grid item xs className={classes.cardpadding} key={'cat' + index} >
                <Grid item xs={4} className={classes.root}>
                    <Card className={classes.card} onClick={e => handleClick(item.value)}>
                        <CardMedia
                            className={classes.media}
                            image={item.img}
                            title={item.label}
                        />
                        <CardContent className={classes.content}>
                            {item.label}
                        </CardContent>
                    </Card>
                </Grid>
                // </Grid>
            ))}
        </Grid>
    )
}
