import { createStore, applyMiddleware } from 'redux';
import thunk from 'redux-thunk';
import { composeWithDevTools } from 'redux-devtools-extension';
import rootReducer from './reducer/rootReducer';
import { MakeStore, Context, createWrapper } from 'next-redux-wrapper';

const middlewares = [thunk];

const store: MakeStore<typeof rootReducer> = (context: Context) => createStore(rootReducer, composeWithDevTools(applyMiddleware(...middlewares)));

export const wrapper = createWrapper<typeof rootReducer>(store, { debug: true });
