import Link from 'next/link';
import Head from 'next/head';
import { connect } from 'react-redux';
import actions from '../store/actions';
import { Input } from '@material-ui/core';
// const result: any = useSelector(selector: Function, equalityFn ?: Function)
const Layout = ({ children, title, isAuthenticated, deauthenticate, value, handlechange }: any) => {


    return (
        <div>
            <Head>
                <title>{title}</title>
                <meta charSet="utf-8" />
                <meta name="viewport" content="initial-scale=1.0, width=device-width" />
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/bulma/0.6.2/css/bulma.min.css" />
            </Head>
            <div className="tabs is-centered">
                <ul>
                    <Link href="/"><a>Home</a></Link>
                    {!isAuthenticated && <Link href="/signin"><a>Sign In</a></Link>}
                    {!isAuthenticated && <Link href="/signup"><a>Sign Up</a></Link>}
                    {isAuthenticated && <Link href="/users"><a>Profile</a></Link>}
                    {isAuthenticated && <li onClick={deauthenticate}><a>Sign Out</a></li>}
                    <Input
                        className="input"
                        type="password"
                        placeholder="Password"
                        required
                        value={value}
                        onChange={e => handlechange(e.target.value)}
                    />
                </ul>
            </div>

            <div className="has-text-centered">
                {children}
            </div>
        </div>
    )
};

const mapStateToProps = (state: any) => (
    { isAuthenticated: !!state.authentication.token }
);

export default connect(mapStateToProps, actions)(Layout);
