import { connect } from 'react-redux';
import initialize from '../utils/initialize';
import Layout from '../components/Layout';
import Search from './../components/Search';
import actions from '../store/actions';

const Index = (props) => {
    // console.log(props, 'props');
    return (

        <Layout title="Home">
            <Search />
        </Layout>
    )
};

Index.getInitialProps = async function (ctx: any) {
    if (ctx.isServer) {
        if (ctx.store.getState().product.data.length === 0) {
            await ctx.store.dispatch(actions.getProduct());
            console.log(ctx.store.getState(), 'if');
        }
    } else {
        console.log('ctx', ctx.store.getState(), ctx.isServer);
    }

    return {};
};


export default connect(state => state)(Index);
